/// <summary>
/// init
/// </summary>
public void init()
{
    using(var t = CAITelemetry::newRequestForForm(this, this.name()).StartOperation(strFmt('%1.%2',this.args().name(),"init")))
    {
        next init();
    }
}

/// <summary>
/// run
/// </summary>
public void run()
{
    using(var t = CAITelemetry::newRequestForForm(this, this.name()).StartOperation(strFmt('%1.%2',this.args().name(),"run")))
    {
        next run();
    }
}

